SELECT esinetyyppi.Nimi FROM esinetyyppi, pelihahmo, paikat, esine WHERE esinetyyppi.Nimi = "" AND pelihahmo.Pelaaja_Id = paikat.Pelaaja_Id AND esine.Saatavilla = 1 AND esinetyyppi.EsinetyyppiId IN ( SELECT esinetyyppi.EsineTyyppiId FROM esinetyyppi, paasee, pelihahmo, paikat WHERE esinetyyppi.EsineTyyppiId = paasee.avain AND paikat.Pelaaja_Id = pelihahmo.Pelaaja_Id AND paikat.PaikkaId = paasee.tulee )